mapper.select:
select 

from so_info_sap
where SO = #{so,jdbcType=VARCHAR}
  and SO_Item = #{soItem,jdbcType=VARCHAR}

mapper.delete:
delete from so_info_sap
where SO = #{so,jdbcType=VARCHAR}
  and SO_Item = #{soItem,jdbcType=VARCHAR}

mapper.insert:
