  Sold_To, Sale_Org, Sale_Office, 
  Dis_C, SKU, SO_status, 
  Description, Qty, Unit
  )
values (#{so,jdbcType=VARCHAR}, #{soItem,jdbcType=VARCHAR}, #{poNumber,jdbcType=VARCHAR}, 
  #{soldTo,jdbcType=VARCHAR}, #{saleOrg,jdbcType=VARCHAR}, #{saleOffice,jdbcType=VARCHAR}, 
  #{disC,jdbcType=VARCHAR}, #{sku,jdbcType=INTEGER}, #{soStatus,jdbcType=INTEGER}, 
  #{description,jdbcType=VARCHAR}, #{qty,jdbcType=INTEGER}, #{unit,jdbcType=VARCHAR}
  )
